import If, {Else} from "./If";

// eslint-disable-next-line import/no-anonymous-default-export
export default props=>{
    const usuario =props.usuario || {}
    return(
        <div>
            
           <If test={usuario && usuario.nombre}>
                Sea Bienvenido {usuario.nombre}

            </If>
            <If test={!usuario || !usuario.nombre}>
                Sea Bienvenido {'Bro'}
            </If>
